                <Link to="/inventory">Admin</Link>
                 {/* <input type="text" onBlur={handleSearch} className="product-search" placeholder="Search your item"/> */}
                <button onClick={() => setLoggedInUser({})}>Sign out</button>
                {loggedInUser.name}
                 {
                        account ?
                            <Avatar className="avtar2"
                                onClick={handleClick} title={account.fname.toUpperCase()}>{account.fname[0].toUpperCase()}</Avatar> :
                            <Avatar className="avtar"
                                onClick={handleClick} />
                    }
            </nav>
        </div>
    );
};

export default Header;
